function nimg = filtroIdeal(img, d0, w)      [lin, col] = size(img);    d = zeros(lin, col);  for i = 1 : lin    for j = 1 : col      d(i, j) = sqrt(lin^ 2 + col^ 2);    end  end   h = zeros(lin, col);  for i = 1 : lin    for j = 1 : col        if ((d(i,j) >= (d0 - w/2)) && (d(i,j) <= (d0 + w/2)))            h(i, j) = 0;        else            h(i, j) = 1;        end    end  end    nimg = h .* fftshift(fft2(img));  nimg = abs(ifft2(nimg));end